import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  const styles = {
    pastelBackground: {
      backgroundColor: '#fef6f0',
      fontFamily: "'Quicksand', sans-serif",
    },
    heroSection: {
      background: 'linear-gradient(to right, #ffe9ec, #e0f7fa)',
      borderRadius: '20px',
      padding: '60px 20px',
      margin: '20px',
    },
    textSection: {
      textAlign: 'center',
    },
    heading: {
      fontSize: '3rem',
      fontWeight: '700',
    },
    lead: {
      fontSize: '1.2rem',
      marginTop: '1rem',
    },
    pastelBtn: {
      backgroundColor: '#ffd1dc',
      color: '#333',
      border: 'none',
      padding: '10px 20px',
      borderRadius: '8px',
      textDecoration: 'none',
      display: 'inline-block',
      marginTop: '1.5rem',
    },
    pastelBtnHover: {
      backgroundColor: '#ffcad4',
    },
    transparentCard: {
      backgroundColor: 'rgba(255, 255, 255, 0.7)',
      borderRadius: '16px',
      padding: '30px',
      boxShadow: '0 4px 20px rgba(0, 0, 0, 0.05)',
      transition: 'transform 0.3s ease, box-shadow 0.3s ease',
      height: '100%',
    },
    cardHover: {
      transform: 'translateY(-4px)',
      boxShadow: '0 8px 30px rgba(0, 0, 0, 0.1)',
    },
    ctaSection: {
      background: 'linear-gradient(to right, #fbeec1, #d3f8e2)',
      borderRadius: '20px',
      padding: '60px 20px',
      margin: '20px',
    },
    footer: {
      backgroundColor: '#f8f9fa',
      color: '#6c757d',
      padding: '20px',
      textAlign: 'center',
    },
    image: {
      maxWidth: '100%',
      borderRadius: '16px',
      boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
    }
  };

  return (
    <div style={styles.pastelBackground} className="home-page">

      {/* Hero Section */}
      <section style={styles.heroSection}>
        <div className="container">
          <div className="row align-items-center">
            <div className="col-md-6 mb-4 mb-md-0" style={styles.textSection}>
              <h1 style={styles.heading}>Unleash the Chef in You üç≥</h1>
              <p style={styles.lead}>
                Discover, share, and get inspired by homemade recipes from around the world.
              </p>
              <Link to="/allrecipes" style={styles.pastelBtn}>
                Browse Recipes
              </Link>
            </div>
            <div className="col-md-6 text-center">
              <img src="/food-illustration.png" alt="Delicious Food" style={styles.image} />
            </div>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="container py-5">
        <div className="text-center mb-5">
          <h2 className="fw-bold">Why You'll Love It</h2>
        </div>
        <div className="row text-center g-4">
          {[ 
            { title: 'üé® Aesthetic & Clean', desc: 'Enjoy a peaceful, pastel UI that‚Äôs easy on the eyes.' },
            { title: 'üì∑ Share Instantly', desc: 'Add photos, steps, and tips with ease.' },
            { title: 'üë©‚Äçüç≥ Community-Loved', desc: 'Made by and for passionate home cooks.' }
          ].map((feature, idx) => (
            <div className="col-md-4" key={idx}>
              <div style={styles.transparentCard}>
                <h4>{feature.title}</h4>
                <p>{feature.desc}</p>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* How it Works */}
      <section className="container py-5">
        <div className="text-center mb-5">
          <h2 className="fw-bold">How It Works</h2>
          <p className="text-muted">Sharing and discovering recipes is easy as 1, 2, 3!</p>
        </div>
        <div className="row text-center g-4">
          {[ 
            'Register quickly and join our foodie family.',
            'Add your recipe with ingredients, steps & images.',
            'Inspire others to try your creations.'
          ].map((text, idx) => (
            <div className="col-md-4" key={idx}>
              <div style={styles.transparentCard}>
                <h3>{idx + 1}‚É£</h3>
                <p>{text}</p>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* CTA Section */}
      <section style={styles.ctaSection} className="text-center">
        <div className="container">
          <h2 className="fw-bold mb-3">Start Sharing Your Culinary Magic ‚ú®</h2>
          <p>It takes less than a minute to join. Start now!</p>
          <Link to="/register" style={{ ...styles.pastelBtn, marginRight: '10px' }}>
            Register
          </Link>
          <Link to="/addrecipe" className="btn btn-outline-secondary">
            Add Recipe
          </Link>
        </div>
      </section>

      {/* Footer */}
      <footer style={styles.footer}>
        <div className="container">
          <small>Made with üç≤ by Saba ‚Ä¢ ¬© 2025 Recipe Sharing Platform</small>
        </div>
      </footer>
    </div>
  );
};

export default Home;
