import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const EditRecipe = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [title, setTitle] = useState('');
  const [ingredients, setIngredients] = useState('');
  const [instructions, setInstructions] = useState('');
  const [imagePreview, setImagePreview] = useState('');
  const [imageFile, setImageFile] = useState(null);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const res = await axios.get(`/api/recipes/${id}`);
        const recipe = res.data;
        setTitle(recipe.title);
        setIngredients(recipe.ingredients.join(', '));
        setInstructions(recipe.instructions);
        setImagePreview(recipe.image); // Show current image
      } catch (err) {
        console.error('Failed to fetch recipe:', err);
        setError('Failed to fetch recipe data.');
      }
    };

    fetchRecipe();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('title', title);
    formData.append('ingredients', JSON.stringify(ingredients.split(',').map(item => item.trim())));
    formData.append('instructions', instructions);
    if (imageFile) {
      formData.append('image', imageFile);
    }

    try {
      const res = await axios.put(`/api/recipes/${id}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setSuccess('Recipe updated successfully!');
      setError('');
      setTimeout(() => navigate(`/recipes/${id}`), 1500);
    } catch (err) {
      console.error('Update failed:', err);
      setError('Failed to update recipe.');
      setSuccess('');
    }
  };

  return (
    <div
      className="py-5"
      style={{
        background: 'linear-gradient(to right, #f8bbd0, #b2ebf2)',
        minHeight: '100vh',
        fontFamily: "'Quicksand', sans-serif",
      }}
    >
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-md-6 col-sm-10">
            <div
              className="p-3 shadow-sm rounded-3"
              style={{ backgroundColor: 'rgba(255, 255, 255, 0.9)' }}
            >
              <h3 className="text-center mb-3 fw-bold text-dark">Edit Recipe</h3>

              {error && <div className="alert alert-danger">{error}</div>}
              {success && <div className="alert alert-success">{success}</div>}

              <form onSubmit={handleSubmit} encType="multipart/form-data">
                <div className="mb-2">
                  <label className="form-label">Title</label>
                  <input
                    type="text"
                    className="form-control"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    required
                  />
                </div>

                <div className="mb-2">
                  <label className="form-label">Ingredients (comma-separated)</label>
                  <textarea
                    className="form-control"
                    value={ingredients}
                    onChange={(e) => setIngredients(e.target.value)}
                    rows="2"
                    required
                  />
                </div>

                <div className="mb-2">
                  <label className="form-label">Instructions</label>
                  <textarea
                    className="form-control"
                    value={instructions}
                    onChange={(e) => setInstructions(e.target.value)}
                    rows="4"
                    required
                  />
                </div>

                <div className="mb-3">
                  <label className="form-label">Upload New Image (optional)</label>
                  <input
                    type="file"
                    className="form-control"
                    onChange={(e) => setImageFile(e.target.files[0])}
                  />
                  {imagePreview && (
                    <div className="mt-2 text-center">
                      <p className="mb-1">Current Image:</p>
                      <img
                        src={imagePreview}
                        alt="Current"
                        className="img-fluid rounded"
                        style={{ maxHeight: '200px' }}
                      />
                    </div>
                  )}
                </div>

                <div className="text-center">
                  <button type="submit" className="btn btn-outline-success px-4">
                    Update
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EditRecipe;
