import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AddRecipe = () => {
  const navigate = useNavigate();
  const [title, setTitle] = useState('');
  const [ingredients, setIngredients] = useState('');
  const [instructions, setInstructions] = useState('');
  const [imageFile, setImageFile] = useState(null);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('title', title);
    formData.append('ingredients', JSON.stringify(ingredients.split(',').map(item => item.trim())));
    formData.append('instructions', instructions);
    if (imageFile) formData.append('image', imageFile);

    try {
      const res = await axios.post('http://localhost:3001/api/recipes', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      if (res.status === 201 || res.status === 200) {
        setSuccess('Recipe added successfully!');
        setError('');
        setTitle('');
        setIngredients('');
        setInstructions('');
        setImageFile(null);
        setTimeout(() => navigate('/allrecipes'), 1500);
      }
    } catch (err) {
      console.error(err);
      setError('Failed to add recipe.');
      setSuccess('');
    }
  };

  return (
    <div className="container mt-5">
      <h2>Add New Recipe</h2>

      {error && <div className="alert alert-danger mt-3">{error}</div>}
      {success && <div className="alert alert-success mt-3">{success}</div>}

      <form onSubmit={handleSubmit} className="mt-4" encType="multipart/form-data">
        <div className="mb-3">
          <label className="form-label">Title</label>
          <input 
            type="text" 
            className="form-control" 
            value={title} 
            onChange={(e) => setTitle(e.target.value)} 
            required 
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Ingredients (comma-separated)</label>
          <textarea 
            className="form-control" 
            value={ingredients} 
            onChange={(e) => setIngredients(e.target.value)} 
            rows="3" 
            required 
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Instructions</label>
          <textarea 
            className="form-control" 
            value={instructions} 
            onChange={(e) => setInstructions(e.target.value)} 
            rows="5" 
            required 
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Upload Image</label>
          <input 
            type="file" 
            className="form-control" 
            onChange={(e) => setImageFile(e.target.files[0])}
            
          />
        </div>

        <button type="submit" className="btn btn-primary">Add Recipe</button>
      </form>
    </div>
  );
};

export default AddRecipe;
