import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AddRecipe = () => {
  const navigate = useNavigate();
  const [title, setTitle] = useState('');
  const [ingredients, setIngredients] = useState('');
  const [instructions, setInstructions] = useState('');
  const [imageFile, setImageFile] = useState(null);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('title', title);
    formData.append('ingredients', JSON.stringify(ingredients.split(',').map(item => item.trim())));
    formData.append('instructions', instructions);
    if (imageFile) formData.append('image', imageFile);

    try {
      const res = await axios.post('http://localhost:3000/api/recipes', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (res.status === 201 || res.status === 200) {
        setSuccess('Recipe added successfully!');
        setError('');
        setTitle('');
        setIngredients('');
        setInstructions('');
        setImageFile(null);
        setTimeout(() => navigate('/allrecipes'), 1500);
      }
    } catch (err) {
      console.error(err);
      setError('Failed to add recipe.');
      setSuccess('');
    }
  };

  return (
    <div
      style={{
        background: '#fdf6f0', // consistent soft pastel background
        minHeight: '100vh',
        paddingTop: '60px',
        fontFamily: "'Quicksand', sans-serif",
      }}
    >
      <div className="container">
        <h2 className="text-center mb-4 text-dark fw-bold">Add a New Recipe</h2>

        {error && <div className="alert alert-danger text-center">{error}</div>}
        {success && <div className="alert alert-success text-center">{success}</div>}

        <form onSubmit={handleSubmit} className="mx-auto" style={{ maxWidth: '600px' }} encType="multipart/form-data">
          <div className="mb-3">
            <label className="form-label">Title</label>
            <input
              type="text"
              className="form-control"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Ingredients (comma-separated)</label>
            <textarea
              className="form-control"
              value={ingredients}
              onChange={(e) => setIngredients(e.target.value)}
              rows="3"
              required
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Instructions</label>
            <textarea
              className="form-control"
              value={instructions}
              onChange={(e) => setInstructions(e.target.value)}
              rows="5"
              required
            />
          </div>

          <div className="mb-4">
            <label className="form-label">Upload Image</label>
            <input
              type="file"
              className="form-control"
              onChange={(e) => setImageFile(e.target.files[0])}
            />
          </div>

          <div className="text-center">
            <button type="submit" className="btn btn-primary px-4">Add Recipe</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddRecipe;
