import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const RecipeDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [recipe, setRecipe] = useState(null);

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const res = await axios.get(`/api/recipes/${id}`);

        setRecipe(res.data);
      } catch (err) {
        console.error('Failed to fetch recipe:', err);
      }
    };
    fetchRecipe();
  }, [id]);

  const handleDelete = async () => {
    const confirmDelete = window.confirm("Are you sure you want to delete this recipe?");
    if (!confirmDelete) return;

    try {
        await axios.delete(`/api/recipes/${id}`);

      alert("Recipe deleted successfully.");
      navigate('/allrecipes');
    } catch (err) {
      console.error("Failed to delete recipe:", err);
      alert("Could not delete the recipe.");
    }
  };

  const handleEdit = () => {
    navigate(`/editrecipe/${id}`);
  };

  if (!recipe) return <p>Loading recipe...</p>;

  return (
    <div className="container mt-5">
      <h2>{recipe.title}</h2>
      {recipe.image && <img src={recipe.image} alt={recipe.title} className="img-fluid my-3" />}
      
      <h4>Ingredients:</h4>
      <ul>
        {recipe.ingredients.map((item, index) => (
          <li key={index}>{item}</li>
        ))}
      </ul>

      <h4>Instructions:</h4>
      <p>{recipe.instructions}</p>
      <p className="text-muted">Posted on: {new Date(recipe.createdAt).toLocaleDateString()}</p>

      <div className="d-flex gap-2 mt-3">
        <button className="btn btn-warning" onClick={handleEdit}>Edit</button>
        <button className="btn btn-danger" onClick={handleDelete}>Delete</button>
        <button className="btn btn-outline-primary" onClick={() => navigate('/allrecipes')}>‚Üê Back</button>
      </div>
    </div>
  );
};

export default RecipeDetails;
