import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const RecipeDetails = () => {
  const { id } = useParams(); // grabbing recipe ID from the URL
  const [recipe, setRecipe] = useState(null);

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const res = await axios.get(`http://localhost:3000/api/recipes/${id}`);
        setRecipe(res.data);
      } catch (err) {
        console.error('Failed to fetch recipe:', err);
      }
    };
    fetchRecipe();
  }, [id]);

  if (!recipe) return <p>Loading recipe...</p>;

  return (
    <div className="container mt-5">
      <h2>{recipe.title}</h2>
      {recipe.image && <img src={recipe.image} alt={recipe.title} className="img-fluid my-3" />}
      <h4>Ingredients:</h4>
      <ul>
        {recipe.ingredients.map((item, index) => (
          <li key={index}>{item}</li>
        ))}
      </ul>
      <h4>Instructions:</h4>
      <p>{recipe.instructions}</p>
      <p className="text-muted">Posted on: {new Date(recipe.createdAt).toLocaleDateString()}</p>
    </div>
  );
};

export default RecipeDetails;
