// src/pages/AllRecipes.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const AllRecipes = () => {
  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:3001/api/recipes') // ✅ Make sure this matches your backend port
      .then((res) => {
        setRecipes(res.data);
      })
      .catch((err) => {
        console.error('Error fetching recipes:', err);
      });
  }, []);

  return (
    <div className="container mt-4">
      <h2 className="mb-4">All Recipes</h2>
      <div className="row">
        {recipes.map((recipe) => (
          <div className="col-md-4 mb-3" key={recipe._id}>
            <div className="card h-100">
              
              <div className="card-body d-flex flex-column">
                <h5 className="card-title">{recipe.title}</h5>
                <p className="card-text">
                  {recipe.instructions.length > 100
                    ? recipe.instructions.slice(0, 100) + '...'
                    : recipe.instructions}
                </p>

                {/* ✅ UPDATED LINK */}
                <Link to={`/recipedetails/${recipe._id}`} className="btn btn-primary mt-auto">
                  View Recipe
                </Link>
              </div>
            </div>
          </div>
        ))}
        {recipes.length === 0 && <p>No recipes found.</p>}
      </div>
    </div>
  );
};

export default AllRecipes;
